con:config.script:
def counter = Integer.parseInt(testRunner.testCase.getPropertyValue("counter"))
def a = testRunner.testCase.getPropertyValue("a").split(",")
def b = testRunner.testCase.getPropertyValue("b").split(",")

while(counter < a.size() && counter < b.size()){
testRunner.gotoStepByName("add")
def aData = a[counter]
def bData = b[counter]

testRunner.testCase.setPropertyValue("aData", aData)
testRunner.testCase.setPropertyValue("bData", bData)

log.info "aData " + aData
log.info "bData " + bData
log.info "counter " + counter

counter++
}

